<?phpnamespace App\Controllers\Web\Customer;use Quill\Factories\ModelFactory;class CustomerBaseController extends \App\Controllers\Web\AccountBaseController {    function __construct($app = NULL) {        parent::__construct($app);        if ($this->app->user['is_active'] != 1) {            $this->app->slim->redirect($this->app->config('base_url') . 'account/customer/signup/');        }        $this->models = ModelFactory::boot(array('OrderSuborder'));        \Quill\View::share(array('awaiting_feedback' => $this->models->orderSuborder->awaitingFeedbackCount($this->app->user['id'])));    }}